import { HttpClient } from '@angular/common/http';
import { Component,OnInit } from '@angular/core';

@Component({
  selector: 'app-all-feedbacks',
  templateUrl: './all-feedbacks.component.html',
  styleUrls: ['./all-feedbacks.component.css']
})
export class AllFeedbacksComponent implements OnInit {
  feedbackData: any[] = [];
  cnt:number=0;
  Date_Time:any[][]=[];
  APIUrl:string= "http://localhost:3333/api/Feedback";
  constructor(private http:HttpClient){

  }
  ngOnInit(): void {
    this.getFeedbackData();
  }
  getFeedbackData(){
    this.http.get<any[]>(this.APIUrl).subscribe(
      (data)=>{
        this.feedbackData=data;
        console.log(typeof data);
        for(let resp in data){
            //this.Date_Time= (resp).split("T",1);
            const tempArray=(data[resp].date_Time).split("T",2);
            //console.log(tempArray);
            // this.Date_Time[0]=tempArray[0];
            // this.Date_Time[1]=tempArray[1];
            this.Date_Time.push(tempArray);

        }
        console.log(this.Date_Time);
      }
      );
  }
}
